{   
var Range = Max - Min;   
var Rand = Math.random();   
return(Min + Math.round(Rand * Range));   
}   


function acc()
{
var num = GetRandomNum(10000000,99999999);   
	document.getElementById("UserID").value = "M" + num;;   
}
function send_sms(){
   var phone=$('#UserTel').val();
   if(phone.length!=11){
      alert('请检查您的手机号码');
      return false;
   }
    var flag=parseInt($('#send').attr('data-val'));
   if(flag==1){
      return false;
   }
   $.ajax({
         type:'post',
         url:'<?php echo U('Public/send_code_msg');?>',
         dataType: "json",
         data:{'phone':phone},
         success:function(data){
             if(data.status == 0){
                 $('#send').attr('data-val','1');
                 jishi(60);
             }else{
                 alert(data.info);
             }
         }
         })
   return false;
}
function jishi(time){
   time--;
   if(time>0){
      $('#send').val('验证码已经发送，等待'+time+'秒可重新发送');
      setTimeout('jishi('+time+')',1000);
    }else{
       $('#send').attr('data-val','0');
       $('#send').val('发送验证码');
    }
}
</script>